# This file is executed on every boot (including wake-boot from deepsleep)import gcfrom app_config import WLAN_SSID, WLAN_PASSWORDgc.collect()def connect_to_network():    import network    sta_if = network.WLAN(network.STA_IF)    if not sta_if.isconnected():        print('Connecting to network {}'.format(WLAN_SSID))        sta_if.active(True)        sta_if.connect(WLAN_SSID, WLAN_PASSWORD)        while not sta_if.isconnected():            pass    print('Network config:', sta_if.ifconfig())connect_to_network()